import React, {useState, useEffect} from "react";
import moment from "moment";
import { Link } from 'react-router-dom';
import mapImg from '../../images/map.jpg';
import './styles.css';

const When = () => {
    const [finalDate, setFinalDate] = useState('');
    const mapLink = 'https://www.google.com/maps/place/HayLoft+Ukrainian+BBQ/@50.3795108,30.47715,15z/data=!4m5!3m4!1s0x0:0x8f584a62b87e11ee!8m2!3d50.3795189!4d30.4771466';

    const countTime = () => (
        moment(moment('20211002')  - moment()).format('MM/DD/HH/mm/ss')
    );

    const mapClick = () => {
        window.location.href = mapLink;
    };


    useEffect(() => {
        const interval = setInterval(() => {
            setFinalDate(countTime);
        }, 100);

        return() => {
            clearInterval(interval);
        }
    }, []);

    const timeArr = finalDate.split('/');
    const month = timeArr[0];
    const day = timeArr[1];
    const hours = timeArr[2];
    const mins = timeArr[3];
    const sec = timeArr[4];

    return (
        <div id="when">
            <div className="container grey">
                <h2>Октябрь 02, 2021</h2>
                <div className="counterContainer">
                    <div className="time">
                        {month}
                        <br/>
                        <span>months</span>
                    </div>
                    <div className="time">
                        {day}
                        <br/>
                        <span>days</span>
                    </div>
                    <div className="time">
                        {hours}
                        <br/>
                        <span>hours</span>
                    </div>
                    <div className="time">
                        {mins}
                        <br/>
                        <span>minutes</span>
                    </div>
                    <div className="time">
                        {sec}
                        <br/>
                        <span>seconds</span>
                    </div>
                </div>
            </div>
            <div className="container white">
                <p>
                    Lorem Ipsum is simply dummy text
                    of the printing and typesetting industry.
                    Lorem Ipsum has been the industry's
                    standard dummy text ever since the 1500s,
                    when an unknown printer took a galley of type and scrambled
                    it to make a type specimen book. It has survived not only five centuries,
                    but also the leap into electronic typesetting, remaining essentially unchanged.
                </p>
                <img src={mapImg} alt="map" onClick={mapClick} />
            </div>
        </div>
    )
};

export default When;
