import React, {useState, useEffect} from "react";
import moment from "moment";
import './styles.css';

const When = () => {
    const countTime = () => (
        moment(moment('20211002')  - moment()).format('MM/DD/HH/mm/ss')
    );
    const [finalDate, setFinalDate] = useState(countTime);

    useEffect(() => {
        const interval = setInterval(() => {
            setFinalDate(countTime);
        }, 100);

        return() => {
            clearInterval(interval);
        }
    }, []);

    const timeArr = finalDate.split('/');
    const month = timeArr[0];
    const day = timeArr[1];
    const hours = timeArr[2];
    const mins = timeArr[3];
    const sec = timeArr[4];

    return (
        <div className="container grey">
            <h2>Октябрь 2, 2021</h2>
            <div  className="counterContainer">
                <div className="time">
                    {month}
                    <span>month</span>
                </div>
                <div className="time">
                    {day}

                </div>
                <div className="time">{hours}</div>
                <div className="time">{mins}</div>
                <div className="time">{sec}</div>
            </div>
        </div>
    )
};

export default When;
