import React, {useState, useEffect} from "react";
import moment from "moment";
import './styles.css';

const When = () => {
    const countTime = () => (
        moment(moment('20211002')  - moment()).format('MM/DD/HH/mm/ss')
    );
    const [finalDate, setFinalDate] = useState(countTime);

    useEffect(() => {
        const interval = setInterval(() => {
            setFinalDate(countTime);
        }, 100);

        return() => {
            clearInterval(interval);
        }
    }, []);
    return (
        <div className="container">
            <h2>Октябрь 2, 2021</h2>
            <div  className="counterContainer">
                <div className="hoursContainer"></div>
                <div className="minutesContainer"></div>
                <div className="secondsContainer"></div>
                {finalDate}
            </div>
        </div>
    )
};

export default When;
