import React, {useState, useEffect} from "react";
import moment from "moment";
import mapImg from '../../images/map.jpg';
import { sendForm, init } from 'emailjs-com';
import emailData from "../../emailkey";
import './styles.css';
init(emailData.USER_ID);

const When = () => {
    const [finalDate, setFinalDate] = useState('');
    const mapLink = 'https://www.google.com/maps/place/HayLoft+Ukrainian+BBQ/@50.3795108,30.47715,15z/data=!4m5!3m4!1s0x0:0x8f584a62b87e11ee!8m2!3d50.3795189!4d30.4771466';

    const countTime = () => (
        moment(moment('20211002', 'YYYYMMDD')  - moment()).format('MM/DD/HH/mm/ss')
    );

    const mapClick = () => {
        window.location.href = mapLink;
    };


    useEffect(() => {
        const interval = setInterval(() => {
            setFinalDate(countTime);
        }, 100);

        return() => {
            clearInterval(interval);
        }
    }, []);

    const send = (e) => {
        e.preventDefault();
        sendForm(emailData.SERVICE_ID, emailData.TEMPLATE_ID,e.target, emailData.USER_ID)
            .then(function(response) {
                console.log('SUCCESS!', response.status, response.text);
            }, function(err) {
                console.log('FAILED...', err);
            });
    }

    const timeArr = finalDate.split('/');
    const month = timeArr[0];
    const day = timeArr[1];
    const hours = timeArr[2];
    const mins = timeArr[3];
    const sec = timeArr[4];

    const isEndDate = () => month === '0' && day === '0' && hours === '0' && mins === '0' && sec === '0';

    return (
        <div id="when">
            <div className="container grey">
                <h2>Октябрь 02, 2021</h2>
                {
                   !isEndDate() ?
                       (
                           <div className="counterContainer">
                               We are happy
                           </div>
                       )
                       :
                       (
                           <div className="counterContainer">
                               <div className="time">
                                   {month}
                                   <br/>
                                   <span>months</span>
                               </div>
                               <div className="time">
                                   {day}
                                   <br/>
                                   <span>days</span>
                               </div>
                               <div className="time">
                                   {hours}
                                   <br/>
                                   <span>hours</span>
                               </div>
                               <div className="time">
                                   {mins}
                                   <br/>
                                   <span>minutes</span>
                               </div>
                               <div className="time">
                                   {sec}
                                   <br/>
                                   <span>seconds</span>
                               </div>
                           </div>
                       )
                }
            </div>
            <div className="container white">
                <p>
                    Lorem Ipsum is simply dummy text
                    of the printing and typesetting industry.
                    Lorem Ipsum has been the industry's
                    standard dummy text ever since the 1500s,
                    when an unknown printer took a galley of type and scrambled
                    it to make a type specimen book. It has survived not only five centuries,
                    but also the leap into electronic typesetting, remaining essentially unchanged.
                </p>
                <img src={mapImg} alt="map" onClick={mapClick} />
            </div>
            <form className="contact-form" onSubmit={send}>
                <input type="hidden" name="contact_number" />
                <label>Name</label>
                <input type="text" name="user_name" />
                <label>Email</label>
                <input type="email" name="user_email" />
                <label>Message</label>
                <textarea name="message" />
                <input type="submit" value="Send" />
            </form>
        </div>
    )
};

export default When;
